require 'twitter'
require 'pry'
require 'dotenv'
Dotenv.load


#Je crée deux classes
#Une bot twitter pour les méthodes client
#une bot twitter streaming pour les méthodes streaming
class Bot_twitter

  def initialize

    @client = Twitter::REST::Client.new do |config|
      config.consumer_key        = ENV['CONSUMER_KEY1']
      config.consumer_secret     = ENV['CONSUMER_SECRET1']
      config.access_token        = ENV['ACCESS_TOKEN1']
      config.access_token_secret = ENV['ACCESS_TOKEN_SECRET1']
    end
  end

#Fonction envoi d'un message
#Avec recuperation d'entree utilisateur
# et une gestion d'exception pour prevenir le crash
def messages
  begin
    print "Who do you want to send a message to ?: "
    puts "Type the id like that: @username"
    send_message_to = "" + gets.chomp + ""
    print "What do you want to say to #{send_message_to}?: "
    message_content = "" + gets.chomp + ""
    @message = @client.create_direct_message(send_message_to, message_content)
    puts "Message sent"

  rescue  Twitter::Error::Forbidden, Twitter::Error::Unauthorized => e 
    puts "Message not sent. This account do not allow message from non-follower"
  end
end

#Idem sauf que cette fonction contact tout les journalistes donné
# par felix
# IL y  également une gestion d'exception
# Si jamais le bot est kické par twitter...
def messages_journalist(user,content)
  begin
    @user = user
    @content = content
    @message = @client.create_direct_message(@user, @content)
  rescue  Twitter::Error::Forbidden, Twitter::Error::Unauthorized => e 
    puts "Message not sent. This account do not allow message from non-follower"

  end
end

#Fonction like
#Elle prend les dernier tweet de chaque journaliste
#Et elle les like
def like(array)
  @journaliste_array = array
  @journaliste_array.each { |x|
    tweets = @client.user_timeline(x)
    puts tweets
    @client.favorite(tweets)
  }
end


#Cette fonction envoi un tweet à un journaliste
def tweet_to_journalist(array)
  @journaliste_array = array
  @journaliste_array.each { |x|
    tweet_to_journalist = "#{x} Je suis élève à The Hacking Projet une formation gratuite au code et je viens de faire un bot Twitter trop cool !"
    @client.update(tweet_to_journalist)
  }
end
end

#Ma deuxieme class où j'utilise les fonctions stream
class Bot_twitter_Stream

  def initialize
    @client = Twitter::Streaming::Client.new do |config|
      config.consumer_key        = ENV['CONSUMER_KEY1']
      config.consumer_secret     = ENV['CONSUMER_SECRET1']
      config.access_token        = ENV['ACCESS_TOKEN1']
      config.access_token_secret = ENV['ACCESS_TOKEN_SECRET1']
    end
  end

  #Streaming de tweet
  #JE stream les 15 derniers tweets en fonction
  # des mots clés donné en entrée
  def streaming(tab_keywords)

    @topics = tab_keywords

    i = 0
    unless @topics.length == 0
      @client.filter(track: @topics.join(",")) do |object|

        i = i+1
        puts object.text if object.is_a?(Twitter::Tweet)
        if i >15
          puts "=========================================================="
          puts "Voila les 15 derniers Tweets en rapport avec tes mots clés"
          puts "=========================================================="
          abort
        end
      end
    else puts "No keywords"
    end

  end

  #Essai de fonction pour liker des tweets quand ils sortent
  #Pas sur que la methode filter(track : x) soit la plus adaptée

  def like_when_it_comes_out(tab_keywords)
    @journaliste_array = tab_keywords
    @journaliste_array.each { |x|
      @client.filter(track: x) { |object|
        #binding.pry
        puts object.text if object.is_a?(Twitter::Tweet)
        #binding.pry
      }
    }
  end

end


